import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const Registration = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [password2, setPassword2] = useState('');

    const handleUsername = (event) => {
        setUsername(event.target.value);
    };

    const handlePassword = (event) => {
        setPassword(event.target.value);
    };

    const checkPassword = (event) => {
        setPassword2(event.target.value)
        if (event.target.value === password) {
            console.log("Норм")
        } else {
            console.log("Хуита")
        }
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post(
                'https://8080-rohlend1-dictionary-yqtyynege7u.ws-eu97.gitpod.io/registration', 
                { username, password }
                );
            console.log(response.data);
        } catch (error) {
            console.error('Ошибка при авторизации:', error);
        }
    };

    return (
        <div className="login-container">
            <h1>Регистрация</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input type="text" placeholder="Имя пользователя" value={username} onChange={handleUsername} />
                </div>
                <div className="form-group">
                    <input type="password" placeholder="Пароль" value={password} onChange={handlePassword} />
                </div>
                <button type="submit">Войти</button>
            </form>
            <input type="password" placeholder="Повторите пароль" value={password2} onChange={checkPassword} />
        </div>
    );
};

export default Registration;
