import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSpinner } from '@fortawesome/free-solid-svg-icons'
import { useNavigate } from "react-router-dom";
import DeleteDictWords from './DeleteDictWords';
const EditDict = () => {
    const link = "https://8080-rohlend1-dictionary-5jnb5hheiop.ws-eu99.gitpod.io"
    let navigate = useNavigate()
    const [words, setWords] = useState([])
    const [dictWords,setDictWords] = useState([])
    const [search,setSearch] = useState('')
    const [byTranslate,setByTranslate] = useState(false)
    const [actionType,setActionType] = useState(true)
    const [dictName, setDictname] = useState(' ')

    let Authorization = `Bearer ${localStorage.getItem("jwt")}`
    

    const sendDictName = async () => {
        console.log({newName:dictName})
        try {
            const response = await axios.patch(`${link}/dict`,{newName:dictName},{headers:{
            'Authorization':Authorization
        }
        });
            console.log(response)
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };
    
        
    const fetchDictionaries = async () => {
        try {
            const response = await axios.get(`${link}/home`,{headers:{
            'Authorization':Authorization
        }
        }); 
            setDictname(response.data.name);
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };

    const fetchWords = async () => {
        axios.get(`${link}/words/search`,{headers:{
            'Authorization':Authorization
        },
        params:{
            starts_with:"a",
            by_translate:"false"
        }
    }
    ).then(response => (setWords(response.data))).catch(err => console.error('Ошибка:', err));
};

    const fetchDictWords = async () => {
        try {
            const response = await axios.get(`${link}/dict/words`, { headers: {
                'Authorization': Authorization
            }});
    
            setDictWords(response.data);
            deleteUnnecWords(response.data); // Вызываем deleteUnnecWords с полученными данными
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };
    
    const sendDictionatiesWords = async () => {
        try {
            const response = await axios.post(`${link}/dict/add_words`,{ words: dictWords },
                {
                    headers: {
                        'Authorization': Authorization
                    }
                }
            );
            sendDictName()
            navigate("/profile")
            console.log(response.data)
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };
    

    const handleSearch = async () => {  
        if (!search){
            fetchWords()
            return
        }
        console.log(search)
        axios.get(`${link}/words/search`,{headers:{
                    'Authorization':Authorization
                },
                params:{
                    starts_with:search,
                    by_translate:byTranslate
                }
            }
            ).then(response => (setWords(response.data))).catch(err => console.error('Ошибка:', err));
            console.log(words)
        };

    const handleAddWord = (word) => {
        let element = words.find(e => e.value === word)
        setDictWords(prevWords => [...prevWords, element]);
        setWords(prevWords => {
            const updatedWords = prevWords.filter(e => e.value !== word);
            return updatedWords;
          });

      }
      
      const handleDeleteWord = (word) => {
        let element = dictWords.find(e => e.value === word)
        setWords(prevWords => [...prevWords, element]);
        setDictWords(prevWords => {
            const updatedWords = prevWords.filter(e => e.value !== word);
            return updatedWords;
          });
      }

      const deleteUnnecWords = (words1) => {
        words1.forEach(elem => {
            setWords(prevWords => {
                let element = prevWords.find(e => e.value === elem.value);
                const updatedWords = prevWords.filter(e => e.value !== element.value);
                return updatedWords;
            });
        });
    };

      useEffect(() => {
        fetchWords()
        fetchDictWords()
        fetchDictionaries()
    }, []);

    useEffect(()=>{
        let Debounce = setTimeout(()=>{
            handleSearch()
        },300)
        console.log(words)
        return () => clearTimeout(Debounce) 
    },[search])


    return (
        <div className="create-form">
        <div className = "block">
        <div className = "title">Изменение словаря</div>
        <input type='checkbox' defaultChecked={true} onChange={(e)=> {setActionType(e.target.checked)}}/>
        </div>
        <div className='line'>
        <div className = "block">
        {actionType ? (
            <div>
                        <div className = "line">
        <div className = "">Название словаря</div>
        <input type="text"value={dictName} onChange={(event)=> {setDictname(event.target.value)}}></input>
        </div>
        <span class="slider round"></span>
        <div>Поиск по переводу</div>
        <input type='checkbox' defaultChecked={false} onChange={(e)=> {setByTranslate(e.target.checked)}}/>
                                <input value={search} onChange={(e) => {setSearch(e.target.value)}}/>
             {words && words.length > 0 ? (
                     <div className="dictionary-section">
                         <div className="word-list">
                             {words.map((word) => (
                                 <div key={word.value} className="word-item">
                                     <button onClick={()=>handleAddWord(word.value)} value={word.value} className="word-value">{word.value}:{word.translate}</button>
                                 </div>
                             ))}
                         </div>
                     </div>
                 ) : (
                     <FontAwesomeIcon icon={faSpinner} />
                 )}
                 <div className = "block">
                 {dictWords && dictWords.length > 0 ? (
                     <div className="dictionary-section">
                         <div className="word-list">
                             {dictWords.map((word) => (
                                     <button key={word.value} onClick={()=>handleDeleteWord(word.value)} className="word-value">{word.value}:{word.translate}</button>
                             ))}
                         </div>
                     </div>
                 ) : (
                     <div>Добавьте в словарь слова</div>
                 )}
                  <button className = "button" onClick={sendDictionatiesWords} >Добавить</button>
                 </div>
                 </div>
            ):(
                <div>
                <div className = "block">
                <DeleteDictWords/>
            </div>
            </div>
            )}
       
       </div>
       </div>
    </div>
    );
};

export default EditDict;
