import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSpinner } from '@fortawesome/free-solid-svg-icons'

const Search = () => {
        const [search, setSearch] = useState(' ')
        const [answer,setAnswer] = useState([])
        let Authorization = `Bearer ${localStorage.getItem("jwt")}` 
        const handleSearch = async (event) => {
            if (!search){
                return
            }
            try {
                const response = await axios.post(
                    'https://8080-rohlend1-dictionary-dkorwuew5yo.ws-eu98.gitpod.io/dict/search',{starts_with:search
                    // ,byTranslate:""
                },{headers:{
                        'Authorization':Authorization
                    }
                }
                    );
                setAnswer(response.data)
                console.log(response.data)
            } catch (error) {
                console.error('Ошибка:', error);
            }
        };
        
        useEffect(()=>{
            let Debounce = setTimeout(()=>{
                handleSearch()
            },300)
            return () => clearTimeout(Debounce) 
        },[search])

        return (
            <div className="create-form">
                   <div className = "block">
                    <input value={search} onChange={(e) => {setSearch(e.target.value)}}/>
        {answer && answer.length > 0 ? (
                <div className="dictionary-section">
                    <div className="word-list">
                        {answer.map((word) => (
                            <div key={word.value} className="word-item">
                                <button value={word.value} className="word-value">{word.value}:{word.translate}</button>
                            </div>
                        ))}
                    </div>
                </div>
            ) : (
                <FontAwesomeIcon icon={faSpinner} />
            )}
            </div>
        </div>
        );
    };
    
export default Search;
