import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Registration.css';

const Registration = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [password2, setPassword2] = useState('');
    const [checkStatus, setCheckStatus] = useState(false)
    
    useEffect(()=> {
        checkPassword()
    })
    const handleUsername = (event) => {
        setUsername(event.target.value);
    };

    const handlePassword = (event) => {
        setPassword(event.target.value);
        console.log(password)
    };

    const handlePassword2 = () => {
        let input = document.getElementById("pass2")
        input.addEventListener("change",(e)=>{
            setPassword2(e.target.value)
        })
        console.log(password2)
    };

    const checkPassword = () => {
        console.log(checkStatus,password,password2)
        if (password2 === password) {
            setCheckStatus(true)
        } else {
            setCheckStatus(false)
        }
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
    //     if (checkStatus === true) {
    //     try {
    //         const response = await axios.post(
    //             'https://8080-rohlend1-dictionary-cgugkd2mgfs.ws-eu97.gitpod.io/registration', 
    //             { username, password }
    //             );
    //         console.log(response.data);
    //     } catch (error) {
    //         console.error('Ошибка при авторизации:', error);
    //     }
    // } else {
    //     console.error("Пароли не совпадают")
    // }
    };

    return (
        <div className="login-container">
            <h1>Регистрация</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input type="text" placeholder="Имя пользователя" value={username} onChange={handleUsername} />
                </div>
                <div className="form-group">
                    <input type="password" placeholder="Пароль" value={password} onChange={handlePassword} />
                </div>
                <div className="form-group">
                <input type="password" id = "pass2" placeholder="Повторите пароль"  />
                </div>
                <button type="submit" onClick={checkPassword}>Регистрация</button>
            </form>
        </div>
    );
};

export default Registration;
