import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
const Navbar = () => {
    const link = "https://8080-rohlend1-dictionary-pc1qd3ppko2.ws-eu98.gitpod.io"
    const navigate = useNavigate()
    const [user, setUser] = useState('Unknown');
    let Authorization = `Bearer ${localStorage.getItem("jwt")}`
    const fetchDictionaries = async () => {
        try {
            const response = await axios.get(`${link}/me`,{headers:{
            'Authorization':Authorization
        }
        }); 
            setUser(response.data);
            console.log(user)
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };
    const handleLogout = () => {
        localStorage.clear()
        navigate("/login")
    }
    
    useEffect(() => {
        fetchDictionaries();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);
    return (

        <div className="navbar">
            {user ? (
                 <div className="user" onClick={()=>{handleLogout()}}>
                <div>{user.username}</div>
                <div>{user.createdAt}</div>
                </div>
            ) : (
               <i>Пользователь не найден</i>
            )}
        </div>
    );
};

export default Navbar;
