import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSpinner } from '@fortawesome/free-solid-svg-icons'
import './DictEdit.css';
const DictEdit = () => {
    const [words, setWords] = useState([])
    const [dictWords,setDictWords] = useState([])
    let Authorization = `Bearer ${localStorage.getItem("jwt")}`

    const fetchWords = async () => {
        try {
            const response = await axios.get(`https://8080-rohlend1-dictionary-dkorwuew5yo.ws-eu98.gitpod.io/words`,{headers:{
            'Authorization':Authorization
        }
        });
            setWords(response.data);
            console.log(words)
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };

    const ale = (e) => {
        
        console.log(e)
    }

    useEffect(() => {
        fetchWords();
    }, []);
    return (
        <div className="create-form">
        <div className = "block">
        <div className = "title">Создание словаря</div>
        <div className = "center-line">Название:
            <div className = "input">
                {/* <input type="text" onChange={(event)=> {setDictname(event.target.value)}}></input> */}
                </div>
        </div>
        <div className="line">
        <button className = "button" onClick={fetchWords} >Создать</button>
        </div>
        {words && words.length > 0 ? (
                <div className="dictionary-section">
                    <div className="word-list">
                        {words.map((word) => (
                            <div key={word.value} className="word-item">
                                <button onClick={ale(word.value)} className="word-value">{word.value}:{word.translate}</button>
                            </div>
                        ))}
                    </div>
                </div>
            ) : (
                <FontAwesomeIcon icon={faSpinner} />
            )}
    </div>
    </div>
    );
};

export default DictEdit;
