import React, { useState } from 'react';
import axios from 'axios';
import './Registration.css';

const Registration = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [password2, setPassword2] = useState('');
    const [checkStatus, setCheckStatus] = useState(false)
    
    const handlePassword = (event) => {
        const { value } = event.target;
        setPassword(value);
        checkPassword(value, password2);
    };

    const handlePassword2 = (event) => {
        const { value } = event.target;
        setPassword2(value);
        checkPassword(password, value);
    };

    const checkPassword = (pass1, pass2) => {
        if (pass1 === pass2) {
            setCheckStatus(true);
        } else {
            setCheckStatus(false);
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Проверка паролей
        if (checkStatus) {
            try {
                const response = await axios.post(
                    'https://8080-rohlend1-dictionary-cgugkd2mgfs.ws-eu97.gitpod.io/registration',
                    { username, password }
                );
                console.log(response.data);
            } catch (error) {
                console.error('Ошибка при авторизации:', error);
            }
        } else {
            console.error('Пароли не совпадают');
        }
    };

    return (
        <div className="login-container">
            <h1>Регистрация</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input type="text" placeholder="Имя пользователя" value={username} onChange={handleUsername} />
                </div>
                <div className="form-group">
                    <input type="password" placeholder="Пароль" value={password} onChange={handlePassword} />
                </div>
                <div className="form-group">
                    <input type="password" id="pass2" placeholder="Повторите пароль" value={password2} onChange={handlePassword2} />
                </div>
                <button type="submit" disabled={!checkStatus}>
                    Регистрация
                </button>
            </form>
        </div>
    );
};

export default Registration;
