import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSpinner } from '@fortawesome/free-solid-svg-icons'
const DictCreationWords = () => {
    const [words, setWords] = useState([])
    const [dictWords,setDictWords] = useState([])
    const [wordsToDelete, setWordsToDelete] = useState([])
    let Authorization = `Bearer ${localStorage.getItem("jwt")}`
    
    const fetchWords = async () => {
        try {
            const response = await axios.get(`https://8080-rohlend1-dictionary-dkorwuew5yo.ws-eu98.gitpod.io/words`,{headers:{
            'Authorization':Authorization
        }
        });    
                setWords(response.data);
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };


    const fetchDictWords = async () => {
        try {
            const response = await axios.get(`https://8080-rohlend1-dictionary-dkorwuew5yo.ws-eu98.gitpod.io/dict/words`, { headers: {
                'Authorization': Authorization
            }});
    
            setDictWords(response.data);
            deleteUnnecWords(response.data); // Вызываем deleteUnnecWords с полученными данными
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };
    

    const sendDictionatiesWords = async () => {
        console.log(dictWords)
        try {
            const response = await axios.post(`https://8080-rohlend1-dictionary-dkorwuew5yo.ws-eu98.gitpod.io/dict/add_words`,{words:dictWords},{headers:{
            'Authorization':Authorization
        }
        });
            console.log(response)
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    };

    const sendDeleteDictionatiesWords = async () => {
        if (dictWords !== []){
        try {
            const response = await axios.post(`https://8080-rohlend1-dictionary-dkorwuew5yo.ws-eu98.gitpod.io/dict/delete_words`,{words:wordsToDelete},{headers:{
            'Authorization':Authorization
        }
        });
            // sendDictName()
            console.log(response.data)
        } catch (error) {
            console.error('Ошибка при получении данных словарей:', error);
        }
    } else {
        return
    }
    };

    const handleAddWord = (word) => {
        let element = words.find(e => e.value === word)
        setDictWords(prevDictWords => [...prevDictWords, element]);
        setWords(prevWords => {
            const updatedWords = prevWords.filter(e => e.value !== word);
            return updatedWords;
          });
          setWordsToDelete(prevDictWords => {
            const updatedDictWords = prevDictWords.filter(e => e.value !== word);
            return updatedDictWords;
          });
      }
      
      const handleDeleteWord = (word) => {
        let element = dictWords.find(e => e.value === word)
        setDictWords(prevDictWords => {
          const updatedDictWords = prevDictWords.filter(e => e.value !== word);
          return updatedDictWords;
        });
          setWordsToDelete(prevDictWords => [...prevDictWords, element]);
        setWords(prevWords => [...prevWords, element]);
      }

      const deleteUnnecWords = (words1) => {
        words1.forEach(elem => {
            setWords(prevWords => {
                let element = prevWords.find(e => e.value === elem.value);
                const updatedWords = prevWords.filter(e => e.value !== element.value);
                return updatedWords;
            });
        });
    };

      useEffect(() => {
        fetchDictWords()
        fetchWords();
        console.log(wordsToDelete)
        // console.log()
    }, []);
    
    return (
        <div className="create-form">
        <div className = "block">
        <div className = "title">Создание словаря</div>
        </div>
        <div className='line'>
        <div className = "block">
        {words && words.length > 0 ? (
                <div className="dictionary-section">
                    <div className="word-list">
                        {words.map((word) => (
                            <div key={word.value} className="word-item">
                                <button onClick={()=>handleAddWord(word.value)} value={word.value} className="word-value">{word.value}:{word.translate}</button>
                            </div>
                        ))}
                    </div>
                </div>
            ) : (
                <FontAwesomeIcon icon={faSpinner} />
            )}
            </div>
            <div className = "block">
            {dictWords && dictWords.length > 0 ? (
                <div className="dictionary-section">
                    <div className="word-list">
                        {dictWords.map((word) => (
                                <button key={word.value} onClick={()=>handleDeleteWord(word.value)} className="word-value">{word.value}:{word.translate}</button>
                        ))}
                    </div>
                </div>
            ) : (
                <div>Добавьте в словарь слова</div>
            )}
            </div>
            </div>
            <button className = "button" onClick={sendDictionatiesWords} >Создать</button>
    </div>
    );
};

export default DictCreationWords;
