import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSpinner } from '@fortawesome/free-solid-svg-icons'

const Search = () => {
        const link = "https://8080-rohlend1-dictionary-rzvzlo3zpn6.ws-eu98.gitpod.io"
        const [search, setSearch] = useState('')
        const [answer,setAnswer] = useState([])
        const [byTranslate,setByTranslate] = useState(false)
        let Authorization = `Bearer ${localStorage.getItem("jwt")}`
        const handleSearch = async () => {  
            if (!search){
                return
            }
            axios.get(`${link}/dict/search`,{headers:{
                        'Authorization':Authorization
                    },
                    params:{
                        starts_with:search,
                        by_translate:byTranslate
                    }
                }
                ).then(response => (setAnswer(response.data))).catch(err => console.error('Ошибка:', err));
            };
        
        useEffect(()=>{
            let Debounce = setTimeout(()=>{
                handleSearch()
            },300)
            return () => clearTimeout(Debounce) 
        },[search])

        return (
            <div className="create-form">
                   <div className = "block">
                    <input value={search} onChange={(e) => {setSearch(e.target.value)}}/>
                    <input type='checkbox' defaultChecked={false} onChange={(e)=> {setByTranslate(e.target.checked)}}/>
        {answer && answer.length > 0 ? (
                <div className="dictionary-section">
                    <div className="word-list">
                        {answer.map((word) => (
                            <div key={word.value} className="word-item">
                                <button value={word.value} className="word-value">{word.value}:{word.translate}</button>
                            </div>
                        ))}
                    </div>
                </div>
            ) : (
                <FontAwesomeIcon icon={faSpinner} />
            )}
            </div>
        </div>
        );
    };
    
export default Search;
